// Import necessary modules
import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Define the main App component
const App = () => {
    const [books, setBooks] = useState([]); // State for storing book data

    // Fetch books from the API when the component loads
    useEffect(() => {
        axios.get('http://localhost:7143/api/books') // Adjust to https if needed
            .then(response => setBooks(response.data)) // Store data in the state
            .catch(error => console.error('Error fetching books:', error)); // Log errors
    }, []);

    return (
        <div>
            <h1>Library Books</h1>
            <ul>
                {books.map(book => (
                    <li key={book.id}>{book.name} ({book.year})</li> // Display book name and year
                ))}
            </ul>
        </div>
    );
};

export default App;
