import React, { useState } from 'react';
import axios from 'axios';

function ReservationForm({ book }) 
{
    const [type, setType] = useState("Book");
    const [quickPickUp, setQuickPickUp] = useState(false); // these are just default values
    const [daysReserved, setDaysReserved] = useState(1);

    function handleReserve() 
    {
        axios.post('http://localhost:5207/api/reservations/reserve', // posts the info of the reservation to the backend
            {
            userId: 1, // gotta change it to userId when I would make user verification
            bookId: book.id,
            type: type,
            quickPickUp: quickPickUp,
            daysReserved: daysReserved
            })
        .then(response => 
        {
            alert('Reservation successful');
        })
        .catch(error => 
        {
            console.error("Error making the reservation", error);
        });
    }

    return (
        <div className="p-4">
            <h2 className="text-xl font-bold">Reserve {book.name}</h2>
            <div className="mt-4">
                <label className="block mb-2">Type</label>
                <select value={type} onChange={function(e) { setType(e.target.value); }} className="border rounded p-2">
                    <option value="Book">Book</option>
                    <option value="Audiobook">Audiobook</option>
                </select>
            </div>
            <div className="mt-4">
                <label className="block mb-2 p-4">Quick Pickup</label>
                <input type="checkbox" checked={quickPickUp} onChange={function() { setQuickPickUp(!quickPickUp); }} />
            </div>
            <div className="mt-4">
                <label className="block mb-2 p-4">Days Reserved</label>
                <input type="number" value={daysReserved} onChange={function(e) { setDaysReserved(e.target.value); }} className="border rounded p-2" />
            </div>
            <button onClick={handleReserve} className="mt-4 bg-green-500 text-white py-2 px-4 rounded">
                Confirm Reservation
            </button>
        </div>
    );
}

export default ReservationForm;
