import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ReservationForm from './ReservationForm'; 

const UserBooks = () => {
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch reservations from the API
    const fetchReservations = async () => {
        setLoading(true); // Start loading state
        setError(null); // Reset any previous errors

        try {
            const response = await axios.get('http://localhost:5207/api/reservations');

            // Check if the response data is empty
            if (response.data && response.data.length > 0) {
                setReservations(response.data); // Set reservations if data is available
            } else {
                setReservations([]); // Clear reservations if no data
                setError('No reservations found.'); // Set error message
            }
        } catch (err) {
            setError('Failed to fetch reservations. Please try again later.'); // Handle error
            console.error("Error fetching reservations:", err);
        } finally {
            setLoading(false); // End loading state
        }
    };

    // Fetch reservations when the component mounts
    useEffect(() => {
        fetchReservations();
    }, []);

    // Function to handle successful reservation
    const handleReservationSuccess = () => {
        fetchReservations(); // Re-fetch reservations after a successful reservation
    };

    if (loading) {
        return <div className="p-4">Loading...</div>; // Loading message
    }

    if (error) {
        return <div className="p-4 text-red-600">{error}</div>; // Error message
    }

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">My Reservations</h1>
            <ReservationForm book={{ id: 1, name: "Sample Book" }} onReservationSuccess={handleReservationSuccess} />
            {reservations.length === 0 ? (
                <p>No reservations found.</p> // Message when no reservations exist
            ) : (
                <table className="min-w-full border-collapse border border-gray-200">
                    <thead>
                        <tr>
                            <th className="border border-gray-200 p-2">Book</th>
                            <th className="border border-gray-200 p-2">Reservation Date</th>
                            <th className="border border-gray-200 p-2">Total Cost</th>
                        </tr>
                    </thead>
                    <tbody>
                        {reservations.map((reservation) => (
                            <tr key={reservation.id}>
                                <td className="border border-gray-200 p-2">{reservation.book?.name || "Unknown"}</td>
                                <td className="border border-gray-200 p-2">{new Date(reservation.reservationDate).toLocaleDateString()}</td>
                                <td className="border border-gray-200 p-2">â‚¬{reservation.totalCost.toFixed(2)}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default UserBooks;
